import React, { Component } from 'react';
import authService from '../services/auth.service';
import './ViewAllUserDetails.css';

class ListEmployeeComponent extends Component {

    constructor(props){
        super(props)
        this.handleLogin = this.handleLogin.bind(this);
        this.state={
            reservations: [],
            email:" "

        }   
    }

componentDidMount(){
     
    let user = authService.getCurrentUser(); 
    this.state.email=user.email; 
    authService.getAllReservationToHandleReturns().then((res) => {
          this.setState({reservations:res.data});
    });
}

handleLogin(e) {
    e.preventDefault();

    this.setState({
      message: "",
      loading: true
    });

    this.form.validateAll();

    if (this.checkBtn.context._errors.length === 0) {
      AuthService.login(this.state.username, this.state.password).then(
        () => {
          this.props.history.push("/adminHomepage");
          window.location.reload();
        },
        error => {
          const resMessage =
            (error.response &&
              error.response.data &&
              error.response.data.message) ||
            error.message ||
            error.toString();
              if(resMessage=="Request failed with status code 401"){
                this.setState({
                  loading: false,
                  message: "Username or Password is incorrect, Check again"
                });
              }else{
                this.setState({
                  loading: false,
                  message: resMessage
                });
              }
          
        }
      );
    } else {
      this.setState({
        loading: false
      });
    }
  }

    render() {
        return (
            <div>
               <h2 id="headerTitle1">Reservation List+{this.state.email}</h2> 
            <br></br>
               <div className="rowV">
                   <table className="table table-striped table-boordered">
                       <thead>
                           <tr>
                               <th>Email</th>
                               <th>Username</th>
                               <th>Date Of Birth</th>
                               <th>Action</th>
                              
                           </tr>
                           
                       </thead>
                       <tbody>
                           {
                               this.state.reservations.map(
                                reservation =>
                                   <tr key={reservation.id}>
                                       <td>{reservation.bookName}</td>
                                       <td>{reservation.author}</td>
                                       <td>{reservation.returnDate}</td>
                                       
                                       <td>
                                       <button className="buttonV"
              onClick={this.handleLogin}
              disabled={this.state.loading}
            >
              {this.state.loading && (
                <span className="spinner-border spinner-border-sm"></span>
              )}
             <span>Book</span>
            </button>
            </td>
                                       
                                   </tr>
                               )
                           }
                       </tbody>
                   </table>

               </div>
            </div>
        );
    }
}

export default ListEmployeeComponent;
