import React, { Component } from 'react';
import authService from '../services/auth.service';
import './ViewSelectedBook.css';
import CardItem from "../CardItem";
class ViewSelectedBook extends Component {

    constructor(props){
        super(props)
        this.state={
            id:this.props.match.params.id,
            author: '',
            category: '',
            description: ''
           
        }   
    }

    componentDidMount(){
        authService.getBooksByID(this.state.id).then(  (res) =>{
            let book = res.data;
            this.setState({author: book.id,
                category: book.author,
                description : book.title,
                image:book.imageOfVideo
            });
        
        });
        
    }

    render() {
        return (
            <div>
                <br></br>
               <h2 id="headerTitle1">{this.state.description}</h2> 
               <br></br>
              
               
               <div class="child "  >
            <CardItem
              src={this.state.image}
              text={this.state.title}
              
              path={'/viewSelectedBook/'+this.state.id}
            />   
            </div> 
              
            </div>
        );
    }
}

export default ViewSelectedBook;
