import CardItem from "../CardItem";
import "../Cards.css";
import "./BookCart.css";
import React, { Component } from 'react';
import authService from '../services/auth.service';


export default class AddNewBook extends Component {

  constructor(props){
    super(props);
    //this.handleClickOpen = this.handleClickOpen.bind(this);
    //this.handleClose = this.handleClose.bind(this);
    
    this.state={
        //open:false,
        reservations: [],
        books:[],
        email:" ",
        price:" "
    };
}

  componentDidMount(){

    
    let user = authService.getCurrentUser(); 
     

    authService.getBookReservationCart(user.email).then((res) => {
          this.setState({reservations:res.data});
    });

    authService.getCheckOutTotalPrice(user.email).then((res) => {
        this.setState({price:res.data});
  });

   
  }

editEmployee(id){
  this.props.history.push(`viewSelectedBook/${id}`);

}

  render(){
    return (
        <div>
            
      <div className="cards">
      
       <div>
        
            
            <h1>Your Backet Details.</h1>
            <br></br>
            <ul className="cards__items123">
          {
                    this.state.reservations.map(
                reservation =>
          <div class="child cards__items12 "  onClick={ () => this.editEmployee(reservation.id)}>
            <CardItem
              src="https://icat.in/storage/app/public/uploads/banner_158211267013.png"
              text={reservation.bookName}
              label="Book"
            />   
            </div> 
              )
            }  
            </ul>
            <br></br>
            <br></br>
          
          
          
    
        </div>
        <form className="checkOutForm">
        <h1>CheckOut</h1>
      <br></br>
      <div className="cartPayment">
    <p className="lendingFee">Total Lending Fee:</p> 
    <p className="totalFee">Rs. 250. 00</p> 
    <button className="cartPaymentButton">Confirm and Pay now</button>
 
    </div>
     </form>
   
      </div>
   
      </div>
 
    );
  }

}
