import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import './Registration.css';
import ReactDOM from 'react-dom';
import React,{useState} from "react";
import { faEye ,faTimes,faCheck,faEyeSlash} from "@fortawesome/free-solid-svg-icons";

function App() {
    

  return (
<div id="loginform">
        <FormHeader title="Login" />
        <Form />
        <OtherMethods />
      </div>
  );
}
const FormHeader = props => (
  <h2 id="headerTitle">{props.title}</h2>
);


const Form = props => (
 <div>
   <FormInput description="Firstname" placeholder="Enter your firstname" type="text" />
   <FormInput description="Lastname" placeholder="Enter your lastname" type="text" />
   <FormInput description="Email" placeholder="Enter your email" type="text" />
   <PasswordValidation description="Email" />

   <FormInput description="Date of Birth" placeholder="Enter your date of birth" type="date"  InputLabelProps={{shrink: true,}} />
   <FormInput description="Password" placeholder="Enter your password" type="password"/>
   <FormInput description="Password" placeholder="Confirm your password" type="password"/>
   <FormButton title="Register"/>
 </div>
);

const FormButton = props => (
<div id="button" class="row">
  <button>{props.title}</button>
</div>
);

const FormInput = props => (
<div class="row">
  <label>{props.description}</label>
  <input type={props.type} placeholder={props.placeholder}/>
</div>  
);



    
        const invalid=(item,v_icon,inv_icon)=>{
    
        }
        
    
        //handle input
        const handleInputChange = e =>{
            const password=e.target.value;
            if(password.match(/[A-Z]/) != null){
                valid("capital","fa-check","fa-times");
            }else{
                invalid("capital","fa-check","fa-times");
            }
            if(password.match(/[0-9]/) != null){
                valid("num","fa-check","fa-times");
            }else{
                invalid("num","fa-check","fa-times");
            }
            if(password.match(/[!@#$%^&*]/) != null){
                valid("char","fa-check","fa-times");
            }else{
                invalid("char","fa-check","fa-times");
            }
            if(password.length > 7){
                valid("more8","fa-check","fa-times");
            }else{
                invalid("more8","fa-check","fa-times");
            }
        };
    
        const handleShowhide=() =>{
          setShow(!show);
        };


const PasswordValidation = props => (
    
    <div className="passwordValidation">
    <div className="container">
        <input
        type={show ? "text": "password"}
        className="password"
        placeholder="Enter your password"
        onChange={handleInputChange}
        />
        {show ? (
           <FontAwesomeIcon  onClick={handleShowhide} icon={faEye} id="show_hide" /> 
        ):(
<FontAwesomeIcon 
onClick={handleShowhide}
icon={faEyeSlash} id="show_hide" />
        )

        }
        
<p id="capital">
  <FontAwesomeIcon  className="fa-times icon" icon={faTimes}/>
  <FontAwesomeIcon  className="fa-check icon" icon={faCheck}/>
  <span>Capital Letters</span>
</p>
<p id="char">
  <FontAwesomeIcon  className="fa-times icon" icon={faTimes}/>
  <FontAwesomeIcon  className="fa-check icon" icon={faCheck}/>
  <span>Special Characters</span>
</p>
<p id="num">
  <FontAwesomeIcon className="fa-times icon" icon={faTimes}/>
  <FontAwesomeIcon  className="fa-check icon" icon={faCheck}/>
  <span>Use Numbers</span>
</p>
<p id="more8">
  <FontAwesomeIcon className="fa-times icon" icon={faTimes}/>
  <FontAwesomeIcon  className="fa-check icon" icon={faCheck}/>
  <span>8+ Characters</span>
</p>
    </div>
</div>
    );

const OtherMethods = props => (
<div id="alternativeLogin">
  <label>Or sign in with:</label>
  <div id="iconGroup">
    <Facebook />
    <Twitter />
    <Google />
  </div>
</div>
);

const Facebook = props => (
<a href="#" id="facebookIcon"></a>
);

const Twitter = props => (
<a href="#" id="twitterIcon"></a>
);

const Google = props => (
<a href="#" id="googleIcon"></a>
);

ReactDOM.render(<App />, document.getElementById('root'));

export default App;
