import React, { Component } from 'react';
import authService from '../services/auth.service';
import './ViewAllUserDetails.css';
import {toast} from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { confirmAlert } from 'react-confirm-alert'; 
import 'react-confirm-alert/src/react-confirm-alert.css';
import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
toast.configure();

class ListEmployeeComponent extends Component {

    constructor(props){
        super(props);
        this.handleClickOpen = this.handleClickOpen.bind(this);
        this.handleClose = this.handleClose.bind(this);
        this.state={
            employees: [],
            status:"",
            open:true
        }   
    }
    handleClickOpen(){
        this.state.open=true;
    }
    handleClose(){
        this.state.open=false;
    }

    

componentDidMount(){
    authService.addNewBookIntegration().then((res) => {
          this.setState({employees:res.data});
    });
 
}

notify (){
 
    // Calling toast method by passing string
    toast(this.state.message)
}

DeleteUser(id){
    authService.deleteUser(id).then(
        
        response => {
          this.setState({
            message: response.data.message,
            successful: true
          });
          authService.addNewBookIntegration().then((res) => {
            this.setState({employees:res.data});
      });
        this.notify();
        },
        error => {
          const resMessage =
            (error.response &&
              error.response.data &&
              error.response.data.message) ||
            error.message ||
            error.toString();
            

          this.setState({
            successful: false,
            message: resMessage
          });
          this.notify();
        }
        
      );
}


    render() {
        return (
            <div>
               <h2 id="headerTitle1">Add New Book</h2> 
            <br></br>
               <div className="rowV">
                   <table className="table table-striped table-boordered">
                       <thead>
                           <tr>
                               <th>Book Name</th>
                               <th>Author</th>
                               <th>Category</th>
                               <th>Action</th>
                               
                           </tr>
                           
                       </thead>
                      
                       <tbody>
                           {
                               
                               this.state.employees.map(
                                   employee =>
                                   
                                   <tr key={employee.id}>
                                       
                                       <td> {employee.title}</td>
                                       
                                       <td>{employee.author}</td>
                                       <td>{employee.category}</td>
                                       <td>
                                       <button className="buttonV"
            variant="outlined" onClick={this.handleClickOpen}
              disabled={this.state.loading}
            >
              {this.state.loading && (
                <span className="spinner-border spinner-border-sm"></span>
              )}
             <span>{employee.title == 'exist' ? `Add as a copy ` : 'Add as a new book'}</span>
            </button>
            </td>
                                       
                                   </tr>
                               )
                           }
                       </tbody>
                   </table>
                   <Dialog
        open={open}
        onClose={this.handleClose}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
      >
        <DialogTitle id="alert-dialog-title">
          {"Use Google's location service?"}
        </DialogTitle>
        <DialogContent>
          <DialogContentText id="alert-dialog-description">
            Let Google help apps determine location. This means sending anonymous
            location data to Google, even when no apps are running.
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={this.handleClose}>Disagree</Button>
          <Button onClick={this.handleClose} autoFocus>
            Agree
          </Button>
        </DialogActions>
      </Dialog>
               </div>
            </div>
        );
    }
}
export default ListEmployeeComponent;
