import './Registration.css';
import React, { Component, useState } from "react";
import AuthService from '../services/auth.service';
import Form from "react-validation/build/form";
import Input from "react-validation/build/input";
import CheckButton from "react-validation/build/button";
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
toast.configure()

const required = value => {
    if (!value) {
        return (
            <div className="alert alert-danger" role="alert">
                This field is required!
            </div>
        );
    }
};

export default class updateUser extends Component {
    //create constructor
    constructor(props) {

        super(props);
        this.handleUpdate = this.handleUpdate.bind(this);
        this.onChangeUsername = this.onChangeUsername.bind(this);
        this.onChangePassword = this.onChangePassword.bind(this);
        this.onChangeFirstname = this.onChangeFirstname.bind(this);
        this.onChangeLastname = this.onChangeLastname.bind(this);
        this.onChangeEmail = this.onChangeEmail.bind(this);

        this.state = {
            id: this.props.match.params.id,
            username: "",
            email: "",
            firstName: "",
            lastName: "",
            password: "",
            loading: false,
            message: ""
        };
    }

    //get existing data
    componentDidMount() {
        AuthService.getUserByID(this.state.id).then((res) => {
            let user = res.data;
            this.setState({
                username: user.username,
                firstName: user.firstName,
                lastName: user.lastName,
                email: user.email
            });
        });
    }

    onChangeUsername(e) {
        this.setState({
            username: e.target.value
        });
    }

    onChangeLastname(e) {
        this.setState({
            lastName: e.target.value
        });
    }

    onChangeFirstname(e) {
        this.setState({
            firstName: e.target.value
        });
    }
    onChangeEmail(e) {
        this.setState({
            email: e.target.value
        });
    }

    onChangePassword(e) {
        this.setState({
            password: e.target.value
        });

    }


    handleUpdate(e) {
        e.preventDefault();

        this.setState({
            message: "",
            loading: true
        });

        this.form.validateAll();

        if (this.checkBtn.context._errors.length === 0) {
            AuthService.update(
                this.state.id,
                this.state.username,
                this.state.email,
                this.state.firstName,
                this.state.lastName

            ).then(

                response => {
                    this.setState({
                        message: response.data.message,
                        successful: true
                    });

                    this.props.history.push(`/addProfilePicture/${this.state.email}`);
                    window.location.reload();
                    this.notify();
                },
                error => {
                    const resMessage =
                        (error.response &&
                            error.response.data &&
                            error.response.data.message) ||
                        error.message ||
                        error.toString();

                    this.setState({
                        successful: false,
                        message: resMessage
                    });

                    if (resMessage == "Request failed with status code 500") {
                        this.setState({
                            loading: false,
                            message: "Incorrect email format!"
                        }
                        )
                    };

                    toast.error(this.state.message);
                }

            );

        }

    }

    notify() {

        // Calling toast method by passing string
        toast.success(this.state.message)
    }

    render() {
        return (
            <div>
                <img className='form-img22r' src={"/images/books-1617327_1920.jpg"} alt='register' />
                <div className="form2r">
                    <Form
                        onSubmit={this.handleUpdate}
                        ref={c => {
                            this.form = c;
                        }}>

                        <div class="row1">
                            <h2 id="headerTitle1">Register</h2>

                            <label >Username</label>
                            <Input
                                placeholder="Enter your username"
                                type="text"
                                name="username"
                                value={this.state.username}
                                onChange={this.onChangeUsername}
                                validations={[required]}
                            />

                            <label htmlFor="password">Email</label>
                            <Input
                                placeholder="Enter your email"
                                type="text"
                                name="email"
                                value={this.state.email}
                                onChange={this.onChangeEmail}
                                validations={[required]}
                            />

                            <label >First Name</label>
                            <Input
                                placeholder="Enter your birthday"
                                type="text"
                                name="firstName"
                                value={this.state.firstName}
                                onChange={this.onChangeFirstname}
                                validations={[required]}
                            />

                            <label >Last Name</label>
                            <Input
                                placeholder="Enter your birthday"
                                type="text"
                                name="lastName"
                                value={this.state.lastName}
                                onChange={this.onChangeLastname}
                                validations={[required]}
                            />

                        </div>

                        <br></br>

                        <div className="row1 ">

                            <button
                                className="btn btn-primary btn-block"
                                disabled={this.state.loading}>
                                {this.state.loading && (
                                    <span className="spinner-border spinner-border-sm"></span>
                                )}
                                <span>Regsiter</span>
                            </button>

                            <br></br>
                            <br></br>

                            <span className='form-input-login'>
                                Already have an account? Login <a href='login'>here</a>
                            </span>

                        </div>

                        <br></br>

                        <CheckButton
                            style={{ display: "none" }}
                            ref={c => {
                                this.checkBtn = c;
                            }}
                        />

                    </Form>

                </div>
            </div>
        );
    }
}
