import './ViewSelectedVideo.css';
import React, { Component } from "react";
import AuthService from '../services/auth.service';
import Form from "react-validation/build/form";
import TextField from '@material-ui/core/TextField';
import CheckButton from "react-validation/build/button";
import { data } from 'jquery';
import {toast} from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
toast.configure()



const required = value => {
  if (!value) {
    return (
      <div className="alert alert-danger" role="alert">
        This field is required!
      </div>
    );
  }
};

export default class ViewSelectedBook extends Component {
  
  constructor(props) {
    super(props);
    this.handleLogin = this.handleLogin.bind(this);
    this.handleLogin2 = this.handleLogin2.bind(this);
    this.handleSearch = this.handleSearch.bind(this);
    
    this.state={
      id:this.props.match.params.id,
      username: '',
      firstname: '',
      currentUser:"",
      lastname: " ",
      email:''
  };
  }



  componentDidMount(){
    AuthService.getUserByID(this.state.id).then(  (res) =>{
        let user = res.data;
        this.setState({username: user.username,
            firstname: user.firstname,
           lastname :user.lastname,
            email:user.email
        });
    
    });
     let user = AuthService.getCurrentUser(); 
     this.state.currentUser=user.username;
}



handleSearch(e){
    
    
    this.props.history.push(`/viewAllVideoReservationAdvance/${this.state.id}`);
      window.location.reload();

}

  handleLogin(e) {
    e.preventDefault();

    this.setState({
      message: "",
      loading: true
    });

    this.form.validateAll();

    
      let user= AuthService.getCurrentUser();
      AuthService. addNewVideoReservation(
        this.state.description,
        user.email,
        user.id,
        this.state.id,
        this.state.image
        
      ).then(
        
        response => {
          this.setState({
            message: response.data.message,
            successful: true
          });
        this.notify();
        },
        error => {
          const resMessage =
            (error.response &&
              error.response.data &&
              error.response.data.message) ||
            error.message ||
            error.toString();
            

          this.setState({
            successful: false,
            message: resMessage
          });
          toast.error(this.state.message);
        }
        
      );
  
  }

  handleLogin2(e) {
    e.preventDefault();

    this.setState({
      message: "",
      loading: true
    });

    this.form.validateAll();

    
      let user= AuthService.getCurrentUser();
      AuthService. addNewVideoReservationOnline(
        this.state.description,
        user.email,
        user.id,
        this.state.id,
        this.state.image
        
      ).then(
        
        response => {
          this.setState({
            message: response.data.message,
            successful: true
          });
        this.notify();
        },
        error => {
          const resMessage =
            (error.response &&
              error.response.data &&
              error.response.data.message) ||
            error.message ||
            error.toString();
            

          this.setState({
            successful: false,
            message: resMessage
          });
          toast.error(this.state.message)
        }
        
      );
  }

  notify (){
 
    // Calling toast method by passing string
    toast.success(this.state.message)
}

render() {
  
  return (
    
    <div >
          <div className="form235"> 
              <Form className="row2"
                onSubmit={this.handleLogin}
                ref={c => {
                  this.form = c;
                }}
              >
                <h2 id="headerTitle">{this.state.description}</h2>
                <div className="rowBook">
                <img src={this.state.image}
                  className="image"></img>
                <p className="description">{this.state.message1} </p>
                </div>
                <br></br>
                <div className="rowVideo2">
                <p className="para1">Author :</p>
                <p className="para2">Category :</p>
                <p className="para2">Price :</p>
                <p className="para2">18+ movie :</p>
                <p className="category1"><span ></span>{this.state.username}</p>
                <p className="category2"><span ></span>{this.state.firstname}</p>
                <p className="category2"><span ></span>Rs. {this.state.lastname}.00</p>
                <p className="category2"><span ></span>{this.state.username}</p>
                </div>
                <br></br>
                <br></br>
      
                </Form>
              </div>
              </div> 
              
        );
}

}
