import './Payment.css';
import React, { Component } from "react";
import AuthService from '../services/auth.service';
import Form from "react-validation/build/form";
import Input from "react-validation/build/input";
import CheckButton from "react-validation/build/button";
import TextField from '@material-ui/core/TextField';
import {toast} from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
toast.configure()


const required = value => {
  if (!value) {
    return (
      <div className="alert alert-danger" role="alert">
        This field is required!
      </div>
    );
  }
};

export default class ContactUs extends Component {
  
  constructor(props) {
    super(props);
    this.handleLogin = this.handleLogin.bind(this);
    this.onChangeName = this.onChangeName.bind(this);
    this.onChangeEmail = this.onChangeEmail.bind(this);
    this.onChangeMessage = this.onChangeMessage.bind(this);
    this.state = {
      name: "",
      email: "",
      Message: "",
      loading: false,
      message: ""
    };
  }

  onChangeName(e) {
    this.setState({
      name: e.target.value
    });
  }

  onChangeMessage(e) {
    this.setState({
      Message: e.target.value
    });
  }

  onChangeEmail(e) {
    this.setState({
      email: e.target.value
    });
  }

  handleLogin(e) {
    e.preventDefault();

    this.setState({
      message: "",
      loading: true
    });

    this.form.validateAll();

    if (this.checkBtn.context._errors.length === 0) {
      AuthService.contactUs(this.state.name, this.state.email,this.state.Message).then(
        response => {
          this.setState({
            message: response.data.message,
            successful: true
          });
          this.notify();
        },
        error => {
          const resMessage =
            (error.response &&
              error.response.data &&
              error.response.data.message) ||
            error.message ||
            error.toString();

          this.setState({
            loading: false,
            message: resMessage
          });
        }
      );
    } else {
      this.setState({
        loading: false
      });
    }
  }

notify (){
 
    // Calling toast method by passing string
    toast(this.state.message)
}

render() {
  
  return (
    
    <div >
     
    <div className="form3"> 
        <Form className="row"
          onSubmit={this.handleLogin}
          ref={c => {
            this.form = c;
          }}
        >
            <h2 id="headerTitle">Payment</h2>
            
            <label >Payment Reason</label>
            <Input
           
            placeholder="Enter payment reason"
              type="text"
           
              name="name"
              value={this.state.name}
              onChange={this.onChangeName}
              validations={[required]}
            />
         

         
            <label htmlFor="email">Price (Rs.)</label>
            <Input
            
            placeholder="Enter your price"
              type="text"
              
              name="email"
              value={this.state.email}
              onChange={this.onChangeEmail}
              validations={[required]}
            />

        <label>Category</label>

        <FormControl >
        <InputLabel id="demo-simple-select-label">Select Book Category</InputLabel>
        <Select
          labelId="demo-simple-select-label"
          id="demo-simple-select"
          value={this.state.category}
          onChange={this.onChangeCategory}  
          className="formItem"
          placeholder="Select category"
        >
            
          <MenuItem value={"Debit"}><img src="https://cdn3.iconfinder.com/data/icons/flat-icons-web/40/Visa-512.png "  className='imageff'/>Visa</MenuItem>
          <MenuItem value={"Credit"}><img src="https://static.vecteezy.com/system/resources/previews/000/357/048/non_2x/vector-credit-card-icon.jpg"  className='imageff'/>Credit</MenuItem>
          <MenuItem value={"Classic"}>Classic</MenuItem>
          <MenuItem value={"Comic and Graphic Novel"}>Comic and Graphic Novel</MenuItem>
          <MenuItem value={"Crime and Detective"}>Crime and Detective</MenuItem>
          <MenuItem value={"Drama"}>Drama</MenuItem>
          
        </Select>
      </FormControl>

             <label htmlFor="message">Card Holder Name</label>
             <Input
            
            placeholder="Enter card holder name"
              type="text"
              
              name="email"
              value={this.state.email}
              onChange={this.onChangeEmail}
              validations={[required]}
            />
            <label htmlFor="message">Card Number</label>
             <Input
            
            placeholder="4569 7589 7596 7581"
              type="text"
              
              name="email"
              value={this.state.email}
              onChange={this.onChangeEmail}
              validations={[required]}
            />
            <div className="rowPayment">
            <label htmlFor="message">Expiry Date</label>
            <label htmlFor="message">CVV</label>
             <Input
            
            placeholder="MM/YY"
              type="text"
              className="expiryDate"
              name="email"
              value={this.state.email}
              onChange={this.onChangeEmail}
              validations={[required]}
            />
            
             <Input
            
            placeholder="758"
              type="text"
              
              name="email"
              value={this.state.email}
              onChange={this.onChangeEmail}
              validations={[required]}
            />
            </div>
            
          <br></br>

          
            <button class="button1"
              className="btn btn-primary btn-block"
              disabled={this.state.loading}
            >
              {this.state.loading && (
                <span className="spinner-border spinner-border-sm"></span>
              )}
             <span>Submit</span>
            </button>
          <br></br>
       
       
          <CheckButton
            style={{ display: "none" }}
            ref={c => {
              this.checkBtn = c;
            }}
          />
        </Form>
        
        </div>
        </div>
  );
}
}
