import './AddNewVideo.css';
import React, { Component,useState } from "react";
import AuthService from '../services/auth.service';
import Form from "react-validation/build/form";
import Input from "react-validation/build/input";
import CheckButton from "react-validation/build/button";
import TextField from '@material-ui/core/TextField';
import { makeStyles } from '@material-ui/core/styles';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import {Link} from 'react-router-dom';
import {toast} from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
toast.configure()


const required = value => {
  if (!value) {
    return (
      <div className="alert alert-danger" role="alert">
        This field is required!
      </div>
    );
  }
};

const options = [
    { value: "hello", label: "hello1" },
    { value: "hello", label: "hello"},
    { value: "hello", label: "hello" },
    { value: "hello", label: "hello" },
    { value: "hello", label: "hello"},
    { value: "hello", label: "hello" },
  ];

export default class AddNewBook extends Component {
  
  constructor(props) {
    
    super(props);
    this.handleLogin = this.handleLogin.bind(this);
    this.onChangeCategory = this.onChangeCategory.bind(this);
    this.onChangeTitle = this.onChangeTitle.bind(this);
    this.onChangeAuthor=this.onChangeAuthor.bind(this);
    this.onChangeEdition=this.onChangeEdition.bind(this);
    this.onChangeIsbn=this.onChangeIsbn.bind(this);
    this.onChangePrice=this.onChangePrice.bind(this);
    this.onChangeNumberofCopies=this.onChangeNumberofCopies.bind(this);
    this.onChangeDate=this.onChangeDate.bind(this);
    this.onChangeBookdes=this.onChangeBookdes.bind(this);
    this.UplaodVideo=this.UplaodVideo.bind(this);
    this.onChangeAgeLimitation=this.onChangeAgeLimitation.bind(this);
    this.onChangePrice=this.onChangePrice.bind(this);
    this.onChangePublishedYear=this.onChangePublishedYear.bind(this);
    
    
    this.state = {
      category:"",
      title:"",
      author:"",
      edition:"",
      isbnNumber:"",
      price:"",
      numberOfCopies:"",
      date:"",
      Message:"",
      ageLimitation:"",
      price:"",
      publishedYear:""
  
    };
  }

  notify (){
 
    // Calling toast method by passing string
    toast(this.state.message)
}

onChangePrice(e) {
  this.setState({
    price: e.target.value
  });
}

onChangePublishedYear(e) {
  this.setState({
    publishedYear: e.target.value
  });
}

  onChangeCategory(e) {
    this.setState({
      category: e.target.value
    });
  }

  onChangeAgeLimitation(e) {
    this.setState({
      ageLimitation: e.target.value
    });
  }

  onChangeBookdes(e){
    this.setState({
      bDes:e.target.value
    });
  }

  onChangeTitle(e) {
    this.setState({
      title: e.target.value
    });
  }
  onChangeAuthor(e) {
    this.setState({
      author: e.target.value
    });
  }


  onChangeEdition(e) {
    this.setState({
      edition: e.target.value
    });

  }
  onChangeIsbn(e) {
    this.setState({
      isbnNumber: e.target.value
    });

  }
  onChangePrice(e) {
    this.setState({
      price: e.target.value
    });

  }
  onChangeDescription(e) {
    this.setState({
      Message: e.target.value
    });

  }
 
  onChangeNumberofCopies(e) {
    this.setState({
      numberOfCopies: e.target.value
    });

  }
  onChangeDate(e) {
    this.setState({
      date: e.target.value
    });

  }
  
  
  UplaodVideo(e){
    this.props.history.push(`/addVideo`);
}

  handleLogin(e) {
    e.preventDefault();

    this.setState({
      message: "",
      loading: true
    });

    this.form.validateAll();

    if (this.checkBtn.context._errors.length === 0) {
      AuthService.addVideoDetails(
        this.state.category,
        this.state.date,
        this.state.price,
        this.state.title,
        this.state.ageLimitation
       
        
   
        
        //this.state.description
        
      ).then(
        response => {
          this.setState({
            message: response.data.message,
            successful: true
          });
          this.notify();
        },
        error => {

          const resMessage =
            (error.response &&
              error.response.data &&
              error.response.data.message) ||
            error.message ||
            error.toString();

          this.setState({
            successful: false,
            message: resMessage
          });
          this.notify();
        }
      );
    }
  }
  

