import React, { Component } from 'react';
import authService from '../services/auth.service';
import './ViewAllUserDetails.css';
import {toast} from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import 'react-confirm-alert/src/react-confirm-alert.css';
toast.configure();


class ListEmployeeComponent extends Component {

    constructor(props){
        super(props);
       
        this.state={
            employees: [],
            status:"",
            isOpen: false,
            message: ""
        }   
    }

componentDidMount(){
  authService.addNewVideoIntegration().then((res) => {
    this.setState({employees:res.data});
});
}


notify (){
 
    // Calling toast method by passing string
    toast(this.state.message)
}

handleAddNewBook(category,date,price,title,ageLimitation,author,publishedYear,numberOfCopies,price1) {

    this.setState({
      message: "",
      loading: true
    });

    if (status === 'new') {
      
      authService. addVideoDetails(category,date,price,title,ageLimitation,author,publishedYear,numberOfCopies,price1).then(
        response => {
          this.setState({
            message: response.data.message,
            successful: true
          });
          this.notify();
        },
        error => {
          const resMessage =
            (error.response &&
              error.response.data &&
              error.response.data.message) ||
            error.message ||
            error.toString();

          this.setState({
            successful: false,
            message: resMessage
          });
          toast.error(this.state.message);
        }
      );
    } else {
      authService. addNewBookCopy(category,title,author,inumber,numberOfCopies,date,bookDescription,bookExcerpt,price,year,numberOfPages).then(
        response => {
          this.setState({
            message: response.data.message,
            successful: true
          });
          this.notify();
        },
        error => {
          const resMessage =
            (error.response &&
              error.response.data &&
              error.response.data.message) ||
            error.message ||
            error.toString();

          this.setState({
            successful: false,
            message: resMessage
          });
          toast.error(this.state.message);
        }
      );
    }
  }


    render() {
        return (
            <div>
               <h2 id="headerTitle1">Add New Book</h2> 
            <br></br>
               <div className="rowV">
                   <table className="table table-striped table-boordered">
                       <thead>
                           <tr>
                               <th>Book Name</th>
                               <th>Author</th>
                               <th>Category</th>
                               <th>Action</th> 
                           </tr>   
                       </thead> 
                       <tbody>
                           {    
                               this.state.employees.map(
                                   employee =>
                                   
                                   <tr key={employee.id}>
                                       
                                       <td> {employee.title}</td>
                                       <td>{employee.author}</td>
                                       <td>{employee.category}{employee.status}</td>
                                       <td>
                                       <button className="buttonV"
            onClick={() => this.handleAddNewBook(employee.category,employee.date,employee.price,employee.title,employee.ageLimitation,employee.author,employee.publishedYear,employee.numberOfCopies,employee.price1)}
              disabled={this.state.loading}
            >  
             <span>{employee.status == 'exist' ? `Add as a copy ` : 'Add as a new book'}</span>
            </button>
            </td>                           
                                   </tr>
                               )
                           }
                       </tbody>
                   </table>
      
               </div>
            </div>
        );
    }
}
export default ListEmployeeComponent;
