import axios from "axios";

const API_URL = "http://localhost:8082/api/auth/";

class AuthService {
  login(username, password) {
    return axios
      .post(API_URL + "signin", {
        username,
        password
      })
      .then(response => {
        if (response.data.accessToken) {
          localStorage.setItem("user", JSON.stringify(response.data));
        }

        return response.data;
      });
  }

  deleteUser(id){
    return axios.delete(API_URL+"deleteUser/"+id);
  }

  updateBook(id){
    this.props.history.push(`/viewSelectedUser/${id}`);
    window.location.reload();
  }

  getUserByID(id){
    return axios.get('http://localhost:8082/api/auth/getUserByID/'+id);
}

  logout() {
    localStorage.removeItem("user");
  }

  getUsers(){
    return axios.get(API_URL+"employees");
    }

  register(username, email,firstName,lastName,password) {
    return axios.post(API_URL + "signup", {
      username,
      email,
      firstName,
      lastName,
      password
    });
  }

  update(username, email,firstName,lastName) {
    return axios.put(API_URL + "updateUser", {
      username,
      email,
      firstName,
      lastName
    });
  }

  getUserProfilePicture(username){
    return axios.get(API_URL+"profilePicture/"+username);
  }

 

  getCurrentUser() {
    return JSON.parse(localStorage.getItem('user'));;
  }
  
 
}

export default new AuthService();
