import './Registration.css';
import React, { Component,useState } from "react";
import AuthService from '../services/auth.service';
import Form from "react-validation/build/form";
import Input from "react-validation/build/input";
import CheckButton from "react-validation/build/button";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faEye ,faTimes,faCheck,faEyeSlash} from "@fortawesome/free-solid-svg-icons";
import {toast} from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
toast.configure()

const required = value => {
  if (!value) {
    return (
      <div className="alert alert-danger" role="alert">
        This field is required!
      </div>
    );
  }
};

export default class AddUser extends Component {

  constructor(props) {
    
    super(props);
    this.handleLogin = this.handleLogin.bind(this);
    this.onChangeUsername = this.onChangeUsername.bind(this);
    this.onChangeFirstname = this.onChangeFirstname.bind(this);
    this.onChangeLastname = this.onChangeLastname.bind(this);
    this.onChangePassword = this.onChangePassword.bind(this);
    this.onChangeEmail=this.onChangeEmail.bind(this);
    
    this.state = {
     
      username: "",
      email:"",
      password: "",
      firstname: "",
      lastname: "",
      message: "",
      loading: false
      
    };
  }

  onChangeUsername(e) {
    this.setState({
      username: e.target.value
    });
  }

  onChangeFirstname(e) {
    this.setState({
      firstname: e.target.value
    });
  }

  onChangeLastname(e) {
    this.setState({
      lastname: e.target.value
    });
  }

  onChangeEmail(e) {
    this.setState({
      email: e.target.value
    });
  }

  onChangePassword(e) {
    this.setState({
      password: e.target.value
    });

  }

  handleLogin(e) {
    e.preventDefault();

    this.setState({
      message: "",
      loading: true
    });

    if (this.checkBtn.context._errors.length === 0) {
      AuthService.register(
        this.state.username,
        this.state.email,
        this.state.firstname,
        this.state.lastname,
        this.state.password
        
      ).then(
        
        response => {
          this.setState({
            message: response.data.message,
            successful: true
          });
          
          this.props.history.push(`/addProfilePicture/${this.state.email}`);
          window.location.reload();
        this.notify();
        },
        error => {
          const resMessage =
            (error.response &&
              error.response.data &&
              error.response.data.message) ||
            error.message ||
            error.toString();
            

          this.setState({
            successful: false,
            message: resMessage
          });
          if(resMessage=="Request failed with status code 500"){
            this.setState({
              loading: false,
              message: "Incorrect email format!"
            }
          )};
          toast.error(this.state.message);
        }
        
      );
      
    
    }
    
  }

  notify (){
    
    // Calling toast method by passing string
    toast.success(this.state.message)
}

render() {
  return (
    
     <div>
       
     <img className='form-img22r'  src={"/images/books-1617327_1920.jpg"} alt='register' /> 
<div className="form2r">
<form>
<div class="row1">

            <h2 id="headerTitle1">Register</h2>
            <label >First Name</label>
            <input
            placeholder="Enter your first name"
            type="text"
            value={this.state.firstname}
            onChange={this.onChangeFirstname}
            validations={[required]}
            name="firstname"/>

<label >Last Name</label>
            <input
            placeholder="Enter your last name"
            type="text"
            value={this.state.lastname}
              onChange={this.onChangeLastname}
              validations={[required]}
            name="lastname"/>

<label >Username</label>
            <input
            placeholder="Enter your username"
            type="text"
            value={this.state.username}
              onChange={this.onChangeUsername}
              validations={[required]}
            name="username"/>

<label >Email</label>
            <input
            placeholder="Enter your email"
            type="text"
            value={this.state.email}
              onChange={this.onChangeEmail}
              validations={[required]}
            name="email"/>

<label >Password</label>
            <input
            placeholder="Enter your password"
            type="password"
            value={this.state.password}
              onChange={this.onChangePassword}
              validations={[required]}
            name="password"/>

      
<button className="btn btn-success" onClick={this.handleLogin} disabled={this.state.loading}>Save</button>
<CheckButton
            style={{ display: "none" }}
            ref={c => {
              this.checkBtn = c;
            }}
          />
             
            
          
          </div>        
        </form>
       
        
        </div>
       </div>
  );
}
}
