import './AboutUs.css';
import React, { Component } from "react";
import Form from "react-validation/build/form";
import AuthService from "../services/auth.service";
import { Redirect } from "react-router-dom";
import {toast} from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
toast.configure()


export default class AdminHomepage extends Component {
 
   

   


render() {
  const hour = this.state;
    if (this.state.redirect) {
        return <Redirect to={this.state.redirect} />
      }
      const { currentUser } = this.state;
      
        return (
     
            <div className="form6"> 
             
            <div >
              <img className='form-img2' src='images/books-1617327_1920.jpg' alt='spaceship' >{currentUser.dateOfBirth}</img>
                <Form className="row23"
                onSubmit={this.handlePages}
                ref={c => {
                  this.form = c;
                }}>
                
                    <h2 id="headerTitle">About Us</h2>
                    <h4> History
Launched in September 2000, BookLender.com is the oldest online book rental service. Founded by veteran business-men Douglas Ross and Andrew Bilinski, BookLender set out to revolutionize the way consumers acquired reading materials online. Oprah Magazine said BookLender is the book version of Netflix. Originally launched as a paperback book rental service of bestselling paperback fiction books, BookLender has expanded into children's books, nonfiction books, and audiobooks. Since 2000, BookLender has grown its selection from 25,000 titles to over a quarter of a million titles.</h4>
                   
                    <br></br>
                    <br></br>
                 
                        
                       
                    
                   
                  
                    
                </Form>
                </div>
             </div>
                
          )
      
    
  
               
}
}
