import './Registration.css';
import AuthService from '../services/auth.service';
import Form from "react-validation/build/form";
import Input from "react-validation/build/input";
import CheckButton from "react-validation/build/button";
import React, { Component } from 'react'
import {toast} from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';



const required = value => {
  if (!value) {
    return (
      <div className="alert alert-danger" role="alert">
        This field is required!
      </div>
    );
  }
};

export default class AddUser extends Component {

  constructor(props) {
    
    super(props);
    this.handleLogin = this.handleLogin.bind(this);
    this.onChangeUsername = this.onChangeUsername.bind(this);
    this.onChangePassword = this.onChangePassword.bind(this);
    
    this.state = {
     
      username: "",
      password: "",
      message: "",
      loading: false
      
    };
  }

  onChangeUsername(e) {
    this.setState({
      username: e.target.value
    });
  }

  onChangePassword(e) {
    this.setState({
      password: e.target.value
    });

  }

  handleLogin(e) {
    e.preventDefault();

    this.setState({
      message: "",
      loading: true
    });

    this.form.validateAll();

    if (this.checkBtn.context._errors.length === 0) {
      AuthService.login(this.state.username, this.state.password).then(
        () => {
          this.props.history.push("/adminHomepage");
          window.location.reload();
       
        },
        error => {
          const resMessage =
            (error.response &&
              error.response.data &&
              error.response.data.message) ||
            error.message ||
            error.toString();
              if(resMessage=="Request failed with status code 401"){
                this.setState({
                  loading: false,
                  message: "Username or Password is incorrect, Check again"
                });
                toast.success(this.state.message);
              }else{
                this.setState({
                  loading: false,
                  message: resMessage
                });
                toast.error(this.state.message);
              }
          
        }
      );
    } else {
      this.setState({
        loading: false
      });
    }
  }

  notify (){
    
    // Calling toast method by passing string
    toast.success(this.state.message)
}

render() {
  return (
    
     <div>
       
     <img className='form-img22r'  src={"/images/books-1617327_1920.jpg"} alt='register' /> 
<div className="form2r">
<form onSubmit={this.handleLogin}>
<div class="row1">

            <h2 id="headerTitle1">Login</h2>
            

<label >Username</label>
            <input
            placeholder="Enter your username"
            type="text"
            value={this.state.username}
              onChange={this.onChangeUsername}
              validations={[required]}
            name="username"/>


<label >Password</label>
            <input
            placeholder="Enter your password"
            type="password"
            value={this.state.password}
              onChange={this.onChangePassword}
              validations={[required]}
            name="password"/>

      

<button className="btn btn-success" type='submit'>Save</button>   
          </div>        
        </form>
        
        
        </div>
       </div>
  );
}
}
