  handleLogin(e) {
    e.preventDefault();

    this.setState({
      message1: "",
      loading: true
    });

    this.form.validateAll();

    if (this.checkBtn.context._errors.length === 0) {
      let user= AuthService.getCurrentUser();
      AuthService. addNewBookReservation(
        this.state.description,
        user.email,
        user.id,
        this.state.id,
        this.state.image
        
        
      ).then(
        
        response => {
          this.setState({
            message1: response.data.message,
            successful: true
          });
        this.notify();
        },
        error => {
          const resMessage =
            (error.response &&
              error.response.data &&
              error.response.data.message) ||
            error.message ||
            error.toString();
            

          this.setState({
            successful: false,
            message1: resMessage
          });
          toast.error(this.state.message1);
        }
        
      );
    } else {
      this.setState({
        loading: false
      });
    }
  }

