import './AdminHomepage.css';
import React, { Component } from "react";
import Form from "react-validation/build/form";
import AuthService from "../services/auth.service";
import { Redirect } from "react-router-dom";



export default class AdminHomepage extends Component {
    constructor(props) {
        super(props);
        this.handlePages = this.handlePages.bind(this);
        this.getGreetingTime=this.getGreetingTime.bind(this);
        this.state = {
          redirect: null,
          userReady: false,
          currentUser: { username: ""},
          currentTime : new Date().toLocaleString()
        };
      }
      componentDidMount() {
        const currentUser = AuthService.getCurrentUser();
        
    
        if (!currentUser) this.setState({ redirect: "/home" });
        this.setState({ currentUser: currentUser, userReady: true })
      }
      handlePages(e){
        e.preventDefault();

        const currentUser = AuthService.getCurrentUser();
         const map1=currentUser.roles;
         const a=map1.map((role, index) =>{return role;});
         if(a=="ROLE_USER"){
            this.props.history.push("/homeUser");
         }
         else{
            this.props.history.push("/homeAdmin");
         }

      }

      getGreetingTime(currentTime)  {
        if (!currentTime || !currentTime.isValid()) { return 'Hello'; }
      
        const splitAfternoon = 12; // 24hr time to split the afternoon
        const splitEvening = 17; // 24hr time to split the evening
        const currentHour = parseFloat(currentTime.format('HH'));
      
        if (currentHour >= splitAfternoon && currentHour <= splitEvening) {
          // Between 12 PM and 5PM
          return 'Good afternoon';
        } else if (currentHour >= splitEvening) {
          // Between 5PM and Midnight
          return 'Good evening';
        }
        // Between dawn and noon
        return 'Good morning';
      }

render() {
    if (this.state.redirect) {
        return <Redirect to={this.state.redirect} />
      }
      const { currentUser } = this.state;
      
        return (
     
            <div className="form5"> 
             
            <div >
              <img className='form-img2' src='images/pexels-lina-kivaka-1741231.jpg' alt='spaceship' />
                <Form className="row"
                onSubmit={this.handlePages}
                ref={c => {
                  this.form = c;
                }}>
                
                    <h2 id="headerTitle">Welcome {currentUser.username}</h2>
                    <h2>Good morning!{this.getGreetingTime(this.state.currentTime)}</h2>
                    <br></br>
                    <br></br>
                    
                    <h3>User Id      :{currentUser.id}
                    <br></br>
                    <br></br>
                    Email Address:{currentUser.email}</h3>
                        
                        <button className="button5"
                      
                        disabled={this.state.loading}
                      >
                        {this.state.loading && (
                          <span className="spinner-border spinner-border-sm"></span>
                        )}
                       <span>{currentUser.roles &&
                      currentUser.roles.map((role, index) => <p key={index}>{role}</p>)}Login</span>
                      </button>
                    
                   
                  
                    
                </Form>
                </div>
             </div>
                
          )
      
    
  
               
}
}
