import React ,{useState,useEffect} from 'react';
import {Link} from 'react-router-dom';
import "./Navbar.css";
import authService from './services/auth.service';
import Dropdown from './Dropdown';



function Navbar() {
    const [click,setClick]=useState(false);
    const [button,setButton]= useState(true);
    const handleClick= () => setClick(!click);
    const closeMobileMenu =() => setClick(false);
    const [dropdown, setDropdown] = useState(false);
   
    const currentUser = checkUsername();
   // const currentUser = authService.getCurrentUser();
  
const [username,setUsername]=useState(currentUser.username);
const [roles,setRole]=useState(currentUser.roles); 
  const logout = () => {
    window.localStorage.clear();
    window.location.href = "/login";
} 


const onMouseEnter = () => {
    if (window.innerWidth < 960) {
      setDropdown(false);
    } else {
      setDropdown(true);
    }
  };

  const onMouseLeave = () => {
    if (window.innerWidth < 960) {
      setDropdown(false);
    } else {
      setDropdown(false);
    }
  };

    const showButton=() => {
        if (window.innerWidth <= 960) {
            setButton(false);
          } else {
            setButton(true);
          }
    };

    useEffect(() => {
        showButton();
      }, []);

    return (
      <>
      <nav className="navbar">
          <div className="navbar-container">
          <Link to="/" className="navbar-logo">
              DOMSEL <i to="/" className='fab fa-typo3' />
              </Link> 
              <div className='menu-icon' onClick={handleClick}>
                 <i className={click ? 'fas fa-times' : 'fas fa-bars'} /> 
              </div>
              <ul className={click ? 'nav-menu active' : 'nav-menu'}>
                  
                  <li className='nav-item'>
                      <Link to='/contact' className='nav-links' onClick={closeMobileMenu}>
                          Contact Us
                      </Link>
                  </li>
                  
                  <li className='nav-item'>
                      <Link to='/rating' className='nav-links' onClick={closeMobileMenu}>
                          About Us
                      </Link>
                  </li>
                  <li className='nav-item'>
                      <Link to='/registerUserselection' className='nav-links' onClick={closeMobileMenu}>
                      {username == null ? `Register ` : ` `}
                      </Link>
                  </li>
                 
                  <li className='nav-item'>
                      <Link to='/login' className='nav-links' onClick={logout}>
                      {username == null ? `Login ` : `LogOut `}
                      </Link>
                  </li>
                  <li className='nav-item'>
                      <Link to='/aboutProfile' className='nav-links userProfile' onClick={closeMobileMenu}>
                         
                         {username != null ? `Hello, ${username}` : ` `}
                      </Link>
                  </li>
                  <li className='nav-item'>
                  <Link to='/aboutProfile' className='nav-links' onClick={closeMobileMenu}>
                     Book
                      </Link>
                  </li>
                  <li className='nav-item'>
                      <Link to='/aboutProfile' className='nav-links' onClick={closeMobileMenu}>
                     Video
                      </Link>
                  </li>
                  <li className='nav-item'>
                      <Link to='/aboutProfile' className='nav-links' onClick={closeMobileMenu}>
                      Payment
                      </Link>
                  </li>
                  <li className='nav-item'>
                      <Link to='/aboutProfile' className='nav-links' onClick={closeMobileMenu}>
                      Lendings
                      </Link>
                  </li>
                  <li className='nav-item'>
                      <Link to='/aboutProfile' className='nav-links' onClick={closeMobileMenu}>
                      Orders
                      </Link>
                  </li>
                  <li className='nav-item'>
                      <Link to='/aboutProfile' className='nav-links' onClick={closeMobileMenu}>
                      {roles == "ROLE_ADMIN" ? `Moderator` : ` `} 
                      </Link>
                  </li>
                  <li className='nav-item' onMouseEnter={onMouseEnter}
            onMouseLeave={onMouseLeave}>
                      <Link to='/aboutProfile' className='nav-links' onClick={closeMobileMenu} >
                      {roles == "ROLE_ADMIN" ? `User` : ` `} <i className='fas fa-caret-down' />
                      </Link>
                  </li>
                  <li className='nav-item'>
                      <Link to='/aboutProfile' className='nav-links' onClick={closeMobileMenu}>
                      <img src="https://i.pinimg.com/originals/d7/d9/6b/d7d96bddc9008591d3574ffedeb11b90.jpg" className='imageff'/>
                      </Link>
                      {dropdown && <Dropdown />}
                  </li>
                  <li
            className='nav-item'
            onMouseEnter={onMouseEnter}
            onMouseLeave={onMouseLeave}
          >
            <Link
              to='/services'
              className='nav-links'
              onClick={closeMobileMenu}
            >
              Services <i className='fas fa-caret-down' />
            </Link>
            {dropdown && <Dropdown />}
          </li>
              
              </ul>
              
              
          </div>
       </nav>
     </>
    );
}

function checkUsername () {
    const currentUser = authService.getCurrentUser();
    // Checks for undefined, null, and a string with no characters:
    if (currentUser && currentUser.username && currentUser.username.length > 0) {
      return currentUser;
    }
    // Default case:
    return 'Anonymous'
  }

export default Navbar
