import './AdvanceBookReservation.css';
import React, { Component } from "react";
import Form from "react-validation/build/form";
import Input from "react-validation/build/input";
import CheckButton from "react-validation/build/button";
import TextField from '@material-ui/core/TextField';
import {toast} from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import authService from '../services/auth.service';
toast.configure()


export default class AddNewBook extends Component {

  constructor(props){
    super(props);
    
    this.handleLogin = this.handleLogin.bind(this);
    this.state={
        //open:false,
        reservations: [],
        books:[],
        email:" ",
        price:" "
    };
}

  componentDidMount(){

    
    let user = authService.getCurrentUser(); 
     this.state.email=user.email;

    
   
  }


handleLogin(e) {
    e.preventDefault();

    
    authService.confirmBookCart(this.state.email, this.state.price).then(
        () => {
          this.props.history.push(`lendingPayment/${this.state.email}`);
          window.location.reload();
        },
        error => {
          const resMessage =
            (error.response &&
              error.response.data &&
              error.response.data.message) ||
            error.message ||
            error.toString();
              if(resMessage=="Request failed with status code 401"){
                this.setState({
                  loading: false,
                  message: "Username or Password is incorrect, Check again"
                });
              }else{
                this.setState({
                  loading: false,
                  message: resMessage
                });
              }
          
        }
      );

   
  }

  render(){
    return (
      <div >
     
      <div className="form3"> 
          <Form className="row"
            onSubmit={this.handleLogin}
            ref={c => {
              this.form = c;
            }}
          >
              <h2 id="headerTitle">Advance Book Reservation+{this.state.email}</h2>
              
              <label >Book Name</label>
              <Input
             
              placeholder="Enter book name"
                type="text"
             
                name="bookName"
                value={this.state.bookName}
                onChange={this.onChangeBookName}
                validations={[required]}
                disabled
              />
           
  
           
              <label htmlFor="email">Email</label>
              <Input
                
                placeholder="Enter user email"
                type="text"
                name="email"
                value={this.state.email1}
                onChange={this.onChangeEmail}
                validations={[required]}
               
              />
  
          <label>Lending Start Date</label>
              <Input
             
              placeholder="Select lending start date"
                type="date"
             
                name="birthday"
                value={this.state.birthday}
                onChange={this.onChangeBirthDay}
                validations={[required]}
              />
  
               
              
            <br></br>
  
            
              <button class="button1"
                className="btn btn-primary btn-block"
                disabled={this.state.loading}
              >
                {this.state.loading && (
                  <span className="spinner-border spinner-border-sm"></span>
                )}
               <span>Submit</span>
              </button>
            <br></br>
         
         
            <CheckButton
              style={{ display: "none" }}
              ref={c => {
                this.checkBtn = c;
              }}
            />
          </Form>
          
          </div>
          </div>
 
    );
  }

}
