import './AdvanceBookReservation.css';
import React, { Component } from "react";
import AuthService from '../services/auth.service';
import Form from "react-validation/build/form";
import Input from "react-validation/build/input";
import CheckButton from "react-validation/build/button";
import TextField from '@material-ui/core/TextField';
import {toast} from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import authService from '../services/auth.service';
toast.configure()


const required = value => {
  if (!value) {
    return (
      <div className="alert alert-danger" role="alert">
        This field is required!
      </div>
    );
  }
};

export default class ContactUs extends Component {
  
  constructor(props) {
    super(props);
    this.handleLogin = this.handleLogin.bind(this);
    this.onChangePrice = this.onChangePrice.bind(this);
    this.onChangeCardType = this.onChangeCardType.bind(this);
    this.onChangeCvv = this.onChangeCvv.bind(this);
    this.onChangecardHolderName = this.onChangecardHolderName.bind(this);
    this.onChangecardNumber = this.onChangecardNumber.bind(this);
    this.onChangeExpiryDate = this.onChangeExpiryDate.bind(this);
    this.onChangeMessage = this.onChangeMessage.bind(this);
    this.state = {
      reason: "",
      price: "",
      cardType: "",
      cardHolderName:"",
      cardNumber:"",
      expiryDate:"",
      cvv:"",
      loading: false,
      message: "",
      email:this.props.match.params.email
    };
  }

  componentDidMount(){
    authService.getPaymentDetailsByEmail(this.state.email).then(  (res) =>{
      let payment = res.data;
      this.setState({reason: payment.reason,
          price: payment.price
      });
  
  });
}

  onChangeReason(e) {
    this.setState({
      reason: e.target.value
    });
  }

  onChangeCardType(e) {
    this.setState({
      cardType: e.target.value
    });
  }

  onChangecardHolderName(e) {
    this.setState({
      cardHolderName: e.target.value
    });
  }

  onChangecardNumber(e) {
    this.setState({
      cardType: e.target.value
    });
  }

  onChangeExpiryDate(e) {
    this.setState({
      cardType: e.target.value
    });
  }

  onChangeCvv(e) {
    this.setState({
      cardType: e.target.value
    });
  }

  onChangePrice(e) {
    this.setState({
      price: e.target.value
    });
  }

  onChangeMessage(e){
      this.setState({
          message:e.target.value
      })
  }

  handleLogin(e) {
    e.preventDefault();

    this.setState({
      message: "",
      loading: true
    });

    this.form.validateAll();

    if (this.checkBtn.context._errors.length === 0) {
      AuthService.addPayment(this.state.cardType, this.state.cardHolderName,this.state.cardNumber,this.state.expiryDate,this.state.cvv,this.state.email).then(
        response => {
          this.setState({
            message: response.data.message,
            successful: true
          });
          this.notify();
        },
        error => {
          const resMessage =
            (error.response &&
              error.response.data &&
              error.response.data.message) ||
            error.message ||
            error.toString();

          this.setState({
            loading: false,
            message: resMessage
          });
        }
      );
    } else {
      this.setState({
        loading: false
      });
    }
  }

notify (){
 
    // Calling toast method by passing string
    toast(this.state.message)
}


render() {
  
  return (
    
    <div >
     
    <div className="form3"> 
        <Form className="row"
          onSubmit={this.handleLogin}
          ref={c => {
            this.form = c;
          }}
        >
            <h2 id="headerTitle">Advance Book Reservation</h2>
            
            <label >Book Name</label>
            <Input
           
            placeholder="Enter book name"
              type="text"
           
              name="bookName"
              value={this.state.reason}
              onChange={this.onChangeReason}
              validations={[required]}
              disabled
            />
         

         
            <label htmlFor="email">Email</label>
            <Input
              
              placeholder="Enter user email"
              type="text"
              name="price"
              value={this.state.price}
              onChange={this.onChangePrice}
              validations={[required]}
              disabled
            />

        <label>Lending Start Date</label>
            <Input
           
            placeholder="Select lending start date"
              type="date"
           
              name="birthday"
              value={this.state.birthday}
              onChange={this.onChangeBirthDay}
              validations={[required]}
            />

             
            
          <br></br>

          
            <button class="button1"
              className="btn btn-primary btn-block"
              disabled={this.state.loading}
            >
              {this.state.loading && (
                <span className="spinner-border spinner-border-sm"></span>
              )}
             <span>Submit</span>
            </button>
          <br></br>
       
       
          <CheckButton
            style={{ display: "none" }}
            ref={c => {
              this.checkBtn = c;
            }}
          />
        </Form>
        
        </div>
        </div>
  );
}
}
