import './AddNewBook.css';
import React, { Component,useState } from "react";
import AuthService from '../services/auth.service';
import Form from "react-validation/build/form";
import Input from "react-validation/build/input";
import CheckButton from "react-validation/build/button";
import TextField from '@material-ui/core/TextField';
import { makeStyles } from '@material-ui/core/styles';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import {Link} from 'react-router-dom';
import {toast} from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
toast.configure()

const required = value => {
  if (!value) {
    return (
      <div className="alert alert-danger alertClass" role="alert">
        This field is required!
      </div>
    );
  }
};


export default class AddNewBook extends Component {
  
  constructor(props) {
    
    super(props);
    this.handleLogin = this.handleLogin.bind(this);
    this.onChangeCategory = this.onChangeCategory.bind(this);
    this.onChangeTitle = this.onChangeTitle.bind(this);
    this.onChangeAuthor=this.onChangeAuthor.bind(this);
    this.onChangeEdition=this.onChangeEdition.bind(this);
    this.onChangePrice=this.onChangePrice.bind(this);
    this.onChangeNumberofCopies=this.onChangeNumberofCopies.bind(this);
    this.onChangeDate=this.onChangeDate.bind(this);
    this.onChangeBookdes=this.onChangeBookdes.bind(this);
    this.onChangeIsbn=this.onChangeIsbn.bind(this);
    this.onChangeBExcerpt=this.onChangeBExcerpt.bind(this);
    this.onChangePrice=this.onChangePrice.bind(this);
    this.onChangePublishedYear=this.onChangePublishedYear.bind(this);
    this.onChangeNumberOfPaged=this.onChangeNumberOfPaged.bind(this);
    this.state = {
      id:this.props.match.params.id,
      category:"",
      title:"",
      author:"",
      edition:"",
      price:"",
      numberOfCopies:"",
      date:"",
      bDes:"",
      inumber:"",
      bExcerpt:"",
      price:"",
      publishedYear:"",
      numberOfPages:""
  
    };
  }

  componentDidMount(){
    AuthService.getBooksByID(this.state.id).then(  (res) =>{
        let book = res.data;
        this.setState({author: book.author,
            category: book.category,
           title : book.title,
            image:book.imageOfVideo,
            bDes:book.bookDescription,
            price:book.price,
      publishedYear:book.year,
      bookExcerpt:book.bookExcerpt,
      numberOfPages:book.numberOfPages,
      bookID:book.id,
      inumber:book.inumber,
      numberOfCopies:book.numberOfCopies,
      bExcerpt:book.bookExcerpt,
      date:book.date
        });
    
    });

}

  onChangeBExcerpt(e) {
    this.setState({
      bExcerpt: e.target.value
    });
  }

  onChangePrice(e) {
    this.setState({
      price: e.target.value
    });
  }

  onChangePublishedYear(e) {
    this.setState({
      publishedYear: e.target.value
    });
  }

  onChangeNumberOfPaged(e) {
    this.setState({
      numberOfPages: e.target.value
    });
  }

  onChangeCategory(e) {
    this.setState({
      category: e.target.value
    });
  }
  onChangeIsbn(e) {
    this.setState({
      inumber: e.target.value
    });
  }
  onChangeBookdes(e){
    this.setState({
      bDes:e.target.value
    });
  }

  onChangeTitle(e) {
    this.setState({
      title: e.target.value
    });
  }
  onChangeAuthor(e) {
    this.setState({
      author: e.target.value
    });
  }


  onChangeEdition(e) {
    this.setState({
      edition: e.target.value
    });

  }
 
  onChangePrice(e) {
    this.setState({
      price: e.target.value
    });

  }
 
  onChangeNumberofCopies(e) {
    this.setState({
      numberOfCopies: e.target.value
    });

  }
  onChangeDate(e) {
    this.setState({
      date: e.target.value
    });

  }
  
  notify (){
 
    // Calling toast method by passing string
    toast.success(this.state.message)
}

  handleLogin(e) {
    e.preventDefault();

    this.setState({
      message: "",
      loading: true
    });

    this.form.validateAll();

    if (this.checkBtn.context._errors.length === 0) {
      AuthService.updateBook(
        this.state.category,
        this.state.title,
        this.state.author,
        this.state.inumber,
        this.state.numberOfCopies,
        this.state.date,
        this.state.bDes,
        this.state.bExcerpt,
        this.state.price,
        this.state.publishedYear,
        this.state.numberOfPages
        
        
        //this.state.description
        
      ).then(
        response => {
          this.setState({
            message: response.data.message,
            successful: true
          });
          this.notify();
        },
        error => {
          const resMessage =
            (error.response &&
              error.response.data &&
              error.response.data.message) ||
            error.message ||
            error.toString();

          this.setState({
            successful: false,
            message: resMessage
          });
          toast.error(this.state.message)
        }
      );
    }
  }
  

