import React from "react";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Typography from "@material-ui/core/Typography";
import CssBaseline from "@material-ui/core/CssBaseline";
import Container from "@material-ui/core/Container";
import Paper from "@material-ui/core/Paper";
import Grid from "@material-ui/core/Grid";
import Divider from "@material-ui/core/Divider";
import { useDropzone } from "react-dropzone";
import RootRef from "@material-ui/core/RootRef";
import { makeStyles } from "@material-ui/core/styles";
import CircularProgress from "@material-ui/core/CircularProgress";
import { green } from "@material-ui/core/colors";
import Button from "@material-ui/core/Button";
import Fab from "@material-ui/core/Fab";
import CheckIcon from "@material-ui/icons/Check";
import CloudUpload from "@material-ui/icons/CloudUpload";
import clsx from "clsx";
import { LinearProgress } from "@material-ui/core";
import axios from "axios";
import CropImage from "./CropImage";
import { useParams } from 'react-router-dom';

const useStyles = makeStyles((theme) => ({
  dropzoneContainer: {
    height: 429,
    background: "#efefef",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    borderStyle: "dashed",
    borderColor: "#aaa",
  },
  preview: {
    width: 250,
    height: 250,
    margin: "auto",
    display: "block",
    marginBottom: theme.spacing(2),
    objectFit: "contain",
  },
  wrapper: {
    margin: theme.spacing(1),
    position: "relative",
  },
  buttonSuccess: {
    backgroundColor: green[500],
    "&:hover": {
      backgroundColor: green[700],
    },
  },
  fabProgress: {
    color: green[500],
    position: "absolute",
    top: -6,
    left: -6,
    zIndex: 1,
  },
  buttonProgress: {
    color: green[500],
    position: "absolute",
    top: "50%",
    left: "50%",
    marginTop: -12,
    marginLeft: -12,
  },
}));

function App() {
 // id:this.props.match.params.id,
  const classes = useStyles();
  const [loading, setLoading] = React.useState(false);
  const [success, setSuccess] = React.useState(false);
  const [file, setFile] = React.useState();
  const [file2, setFile2] = React.useState();
  const [preview, setPreview] = React.useState();
  const [percent, setPercent] = React.useState(0);
  const [downloadUri, setDownloadUri] = React.useState();
  const [downloadUri2, setDownloadUri2] = React.useState();
  const [selectedImageFile, setSelectedImageFile] = React.useState();
  //const [title, setTitle] = React.useState("Syeshmi");
  const { title } = useParams();
  const buttonClassname = clsx({
    [classes.buttonSuccess]: success,
  });
  //const authResult = new URLSearchParams(window.location.search); 
//const title = authResult.get('title')
  const onDrop = React.useCallback((acceptedFiles) => {
    const fileDropped = acceptedFiles[0];
    if (fileDropped["type"].split("/")[0] === "image") {
      setSelectedImageFile(fileDropped);
      return;
    }

    setFile(fileDropped);
    const previewUrl = URL.createObjectURL(fileDropped);
    setPreview(previewUrl);
    setSuccess(false);
    setPercent(0);
  });
  const onDrop2 = React.useCallback((acceptedFiles) => {
    const fileDropped2 = acceptedFiles[0];
    if (fileDropped2["type"].split("/")[0] === "image") {
      setSelectedImageFile(fileDropped2);
      return;
    }

    setFile2(fileDropped2);
    const previewUrl2 = URL.createObjectURL(fileDropped2);
    setPreview(previewUrl2);
    setSuccess(false);
    setPercent(0);
  });

  const { getRootProps, getInputProps,getInputProps2 } = useDropzone({
    multiple: false,
    onDrop,
    onDrop2,
  });

  const { ref, ...rootProps } = getRootProps();

  
  const uploadFile1 = async () => {
    try {
      setSuccess(false);
      setLoading(true);
      const formData2 = new FormData();
      formData2.append("Video", file);
     formData2.append("Title", title);
     formData2.append("Image", file2);
      const API_URL = "http://localhost:8082/api/auth/onlineBook";
      const response = await axios.put(API_URL, formData2, {
        onUploadProgress: (progressEvent) => {
          const percentCompleted = Math.round(
            (progressEvent.loaded * 100) / progressEvent.total
          );
          setPercent(percentCompleted);
        },
      });

      setDownloadUri2(response.data.fileDownloadUri);
      setSuccess(true);
      setLoading(false);
    } catch (err) {
      alert(err.message);
    }
  };

  const onCropSave = ({ file, preview }) => {
    setPreview(preview);
    setFile(file);
    setSuccess(false);
    setPercent(0);
  };

  return (
    <>
      <CssBaseline />
      
      <Toolbar />
      <Toolbar />

      <Container maxWidth="md">
        <Paper elevation={4}>
          <Grid container>
            <Grid item xs={12}>
              <Typography align="center" style={{ padding: 16 }}>
                Upload copy of online book here
              </Typography>
              <Divider />
            </Grid>

            <Grid item xs={6} style={{ padding: 16 }}>
              <RootRef rootRef={ref}>
                <Paper
                  {...rootProps}
                  elevation={0}
                  className={classes.dropzoneContainer}
                >
                  <input {...getInputProps()} />
                  <p>Drag 'n' drop some files here, or click to select files</p>
                </Paper>
              </RootRef>
            </Grid>



            <Grid item xs={6} style={{ padding: 16 }}>
           
            

              {/*  */}
              {file && (
                <>
                  <Divider />
                  <Grid
                    container
                    style={{ marginTop: 16 }}
                    alignItems="center"
                    spacing={3}
                  >
                    <Grid item xs={2}>
                      <div className={classes.wrapper}>
                        <Fab
                          aria-label="save"
                          color="primary"
                          className={buttonClassname}
                          onClick={uploadFile1}
                        >
                          {success ? <CheckIcon /> : <CloudUpload />}
                        </Fab>
                        {loading && (
                          <CircularProgress
                            size={68}
                            className={classes.fabProgress}
                          />
                        )}
                      </div>
                    </Grid>

                    <Grid item xs={10}>
                      {file && (
                        <Typography variant="body">{file.name}</Typography>
                      )}
                      {loading && (
                        <div>
                          <LinearProgress
                            variant="determinate"
                            value={percent}
                          />
                          <div
                            style={{
                              display: "flex",
                              alignItems: "center",
                              justifyContent: "center",
                            }}
                          >
                            <Typography variant="body">{percent}%</Typography>
                          </div>
                        </div>
                      )}

                      {success && (
                        <Typography>
                          File Upload Success!{" "}
                          <a href={downloadUri} target="_blank">
                            File Url
                          </a>
                        </Typography>
                      )}
                    </Grid>
                  </Grid>
                </>
              )}
              {/*  */}
            </Grid>
          </Grid>
        </Paper>
      </Container>
<br></br><br></br>


      

      <CropImage onSave={onCropSave} selectedFile={selectedImageFile} />
    </>
  );
}

export default App;
