import React, { Component } from 'react';
import authService from '../services/auth.service';
import './ViewAllUserDetails.css';

class ListEmployeeComponent extends Component {

    constructor(props){
        super(props)
        
        this.state={
            reservations: [],
            email:" "

        }   
    }

componentDidMount(){
    this.handleLogin = this.handleLogin.bind(this);
    let user = authService.getCurrentUser(); 
    this.state.email=user.email; 
    authService.getAllReservationToHandleReturns().then((res) => {
          this.setState({reservations:res.data});
    });
}


handleLogin(id) {   
          this.props.history.push(`/advanceBookReservation/${id}`);
          window.location.reload();
    
  }

    render() {
        return (
            <div>
               <h2 id="headerTitle1">Reservation List+{this.state.email}</h2> 
            <br></br>
               <div className="rowV">
                   <table className="table table-striped table-boordered">
                       <thead>
                           <tr>
                               <th>Email</th>
                               <th>Username</th>
                               <th>Date Of Birth</th>
                               <th>Action</th>
                              
                           </tr>
                           
                       </thead>
                       <tbody>
                           {
                               this.state.reservations.map(
                                reservation =>
                                   <tr key={reservation.id}>
                                       <td>{reservation.bookName}</td>
                                       <td>{reservation.author}</td>
                                       <td>{reservation.returnDate}</td>
                                       
                                       <td>
                                       <button className="buttonV"
             onClick={this.handleLogin(reservation.bookId)}
              disabled={this.state.loading}
            >
              {this.state.loading && (
                <span className="spinner-border spinner-border-sm"></span>
              )}
             <span>Book</span>
            </button>
            </td>
                                       
                                   </tr>
                               )
                           }
                       </tbody>
                   </table>

               </div>
            </div>
        );
    }
}

export default ListEmployeeComponent;
